import { RouteConfig } from 'react-router-config';
import AppLayout from 'src/components/AppLayout';
import CatalogLayout from 'src/components/CatalogLayout';
import HomeLayout from 'src/components/HomeLayout';
import Cart from 'src/components/Cart';
import Login from 'src/components/Login';
import NotFound, { RedirectTo404 } from 'src/components/NotFound';
import Page from 'src/components/Page';
import Product from 'src/components/Product';
import User from 'src/components/User';

const routes: RouteConfig[] = [
  {
    path: '/404',
    component: NotFound,
  },
  {
    component: AppLayout,
    routes: [
      {
        path: '/',
        exact: true,
        component: HomeLayout,
      },
      {
        path: '/catalog*', // FIXED: use `*`, matchRoutes for SSR does not work with /catalog?search=xxx, only with /catalog/?search=xxx
        component: CatalogLayout,
      },
      {
        path: '/login',
        component: Login,
      },
      {
        path: '/product/:uid',
        component: Product,
      },
      {
        path: '/cart',
        component: Cart,
      },
      {
        path: '/pages/:slug',
        component: Page,
      },
      {
        path: '/user',
        exact: true,
        component: User,
      },
      {
        path: '/user/:key',
        component: User,
      },
      {
        path: '*',
        component: RedirectTo404,
      },
    ],
  },
];

export default routes;

const mq = {
  screenXS: '480px',
  screenSM: '768px',
  screenMD: '992px',
  screenLG: '1200px',
  screenXL: '1600px',
};

export default mq;

import React from 'react';
import { connect } from 'react-redux';
import { Helmet } from 'react-helmet';
import { Layout, Spin } from 'antd';
import { RouteConfig, renderRoutes } from 'react-router-config';
import dispatch from 'src/store/dispatch';
import { getMeta } from 'src/ducks/meta';
import ConfigHelper from 'src/helpers/configHelper';
import Header from 'src/components/Header';
import Footer from 'src/components/Footer';
import { Frame as DevToolsFrame } from 'src/components/DevTools';
import Sprite from 'src/components/Svg/Sprite';

interface IOwnProps {
  route: RouteConfig;
}

interface IConnectedState {
  title: string;
  description: string;
  keywords: string;
  isLoading: boolean;
}

type IProps = IOwnProps & IConnectedState;

interface IState {}

const mapStateToProps = (state: Store.IState) => ({
  title: state.meta.title,
  description: state.meta.description,
  keywords: state.meta.keywords,
  isLoading: state.progress.isLoading,
});

const WrappedAppLayout = connect<IConnectedState, undefined, IOwnProps>(mapStateToProps)(
  class AppLayout extends React.Component<IProps, IState> {
    static fetchData: Store.IFetchDataFn = abortToken => {
      return dispatch(getMeta(abortToken));
    };

    render() {
      const { route: { routes }, title, description, keywords, isLoading } = this.props;
      return (
        <div className="root">
          <style jsx global>{`
            body {
              background-color: white;
              font-family: 'Ubuntu', sans-serif;
            }
            * {
              min-height: 0;
              min-width: 0;
            }
            .app-icon-svg {
              fill: currentColor;
              width: 19px;
              height: 19px;
              pointer-events: none;
            }
            .ant-popover-arrow {
              display: none;
            }
            .app-main-menu {
              max-width: 100%;
              overflow: hidden;
              font-weight: bold;
              letter-spacing: 2px;
              text-transform: uppercase;
              transition: max-width 0.2s;
            }
            .app-main-menu .ant-menu-inline {
              border: 0;
            }
            .app-main-menu .ant-menu-item,
            .app-main-menu .ant-menu-inline .ant-menu-submenu-title,
            .app-main-menu .ant-menu-sub.ant-menu-inline > .ant-menu-item,
            .app-main-menu .ant-menu-sub.ant-menu-inline > .ant-menu-submenu > .ant-menu-submenu-title {
              height: auto;
              line-height: 1;
              padding: 19px 38px;
            }
            .app-main-menu .ant-menu-sub.ant-menu-inline > .ant-menu-item,
            .app-main-menu .ant-menu-sub.ant-menu-inline > .ant-menu-submenu > .ant-menu-submenu-title {
              font-size: 9px;
            }
            .app-main-menu .ant-menu-submenu-title:after {
              right: 23px;
              margin-top: 18px;
            }
            .app-main-menu .ant-menu:not(.ant-menu-horizontal) .ant-menu-item-selected {
              background-color: transparent;
            }
            .app-main-menu .ant-menu-item-selected:after {
              display: none;
            }
            .app-main-menu .ant-menu-submenu-selected .ant-menu-submenu-title {
              color: #ff0959;
            }
            .app-icon-flag-svg {
              width: 16px;
              height: 16px;
            }
            .ant-modal-body {
              padding: 9px 16px 0;
            }
            .ant-modal-content {
              box-shadow: 0 2px 16px 0 rgba(6, 47, 73, 0.5);
            }
            .ant-modal-close {
              display: none;
            }
            .ant-btn {
              text-transform: uppercase;
              letter-spacing: 2px;
            }
            .ant-btn-lg {
              font-size: 11px;
            }
            .ant-tabs-nav .ant-tabs-tab {
              margin-right: 0;
              text-transform: uppercase;
              letter-spacing: 2px;
              font-weight: bold;
              padding-bottom: 13px;
            }
            .ant-select {
              color: #a7b4c3;
            }
            .ant-select-arrow {
              color: #062f49;
            }
            .app-no-image {
              font-size: 16px;
              line-height: 1.375;
              font-weight: bold;
              text-align: center;
              color: #b2bdcb;
              -webkit-font-smoothing: antialiased;
              backface-visibility: hidden;
            }
            .ant-dropdown-menu-item {
              font-size: 14px;
              padding: 10px 20px;
            }
            .ant-badge-count {
              font-family: inherit;
            }
            .ant-btn:hover,
            .ant-btn:focus {
              border-color: transparent;
            }
            .app-text-empty {
              color: #b2bdcb;
              text-align: center;
            }
            .ant-form-item-control {
              padding-left: 2px;
              padding-right: 2px;
            }
            .swiper-button-prev,
            .swiper-button-next {
              height: 45px;
              width: 45px;
              z-index: 2;
              top: 50%;
              background: none;
            }
            .swiper-button-prev {
              left: 10%;
              transform: rotate(-45deg);
            }
            .swiper-button-next {
              right: 10%;
              transform: rotate(135deg);
            }
            .swiper-button-prev::after,
            .swiper-button-prev::before,
            .swiper-button-next::after,
            .swiper-button-next::before {
              content: '';
              position: absolute;
              top: 0;
              left: 0;
              border-width: 7px;
              border-color: #e9ecf1;
              border-radius: 8px;
            }
            .swiper-button-prev::after,
            .swiper-button-next::after {
              border-left-style: solid;
              height: 100%;
            }
            .swiper-button-prev::before,
            .swiper-button-next::before {
              width: 100%;
              border-top-style: solid;
            }
            .app-text-right {
              text-align: right;
            }
            .app-text-center {
              text-align: center;
            }
            .app-section {
              margin-bottom: 24px;
            }
            .app-title {
              margin-bottom: 30px;
              font-size: 24px;
              font-weight: 500;
              letter-spacing: 2px;
              color: #062f49;
              line-height: 19px;
              text-transform: uppercase;
            }
            .app-cart {
              background: transparent;
            }
            .app-cart .ant-card-head {
              height: auto;
              line-height: 1.3;
              border-bottom: 0;
              background: transparent;
            }
            .app-cart .ant-card-head-title {
              font-size: 24px;
              line-height: 1.16;
              color: inherit;
              float: none;
            }
            .app-cart .ant-card-body {
              padding-top: 15px;
            }
            .app-text {
              line-height: 1.7;
            }
            .app-container {
              margin: 0 auto;
              max-width: 900px;
              width: 100%;
              padding: 25px 20px 0;
            }
            .app-image-blur {
              position: absolute;
              top: 50%;
              left: 50%;
              z-index: 1;
              transform: translate(-50%, -50%);
              min-width: 100%;
              min-height: 100%;
              max-height: 100%;
              max-width: 100%;
              filter: blur(20px);
              opacity: 0.9;
              backface-visibility: hidden;
            }
            .app-show-more-button {
              width: 100%;
              display: block;
              font-weight: bold;
            }
            .swiper-slide-duplicate .ant-spin-spinning,
            .swiper-slide-duplicate .ant-spin-blur::after {
              opacity: 0;
            }
            .swiper-slide-duplicate .ant-spin-blur {
              opacity: 1;
              filter: blur(0);
            }
            .layout-spin > .ant-spin-blur {
              overflow: inherit;
            }
          `}</style>
          <style jsx>{`
            .root :global(.layout) {
              height: 100%;
              min-height: 100vh;
              overflow: hidden;
            }
            .root :global(.ant-layout-header) {
              position: relative;
              z-index: 12;
              border-bottom: 1px solid #e5ecf4;
              height: 115px;
              line-height: 1;
            }
            .root :global(.layout .ant-layout-content) {
              display: flex;
            }
            .root :global(.layout-spin),
            .root :global(.layout-spin > .ant-spin-container) {
              flex: auto;
              display: flex;
            }
            .root :global(.layout-spin .ant-spin-blur .ant-spin-spinning),
            .root :global(.layout-spin .ant-spin-blur .ant-spin-blur::after) {
              opacity: 0;
            }
            .root :global(.layout-spin .ant-spin-blur .ant-spin-blur) {
              opacity: 1;
              filter: blur(0);
            }
          `}</style>
          <Helmet>
            <title>{title}</title>
            <meta name="description" content={description} />
            <meta name="keywords" content={keywords} />
          </Helmet>
          <Layout className="layout">
            <Layout.Header>
              <Header />
            </Layout.Header>
            <Layout.Content>
              <Spin spinning={isLoading} size="large" wrapperClassName="layout-spin">
                {renderRoutes(routes)}
              </Spin>
            </Layout.Content>
            <Layout.Footer>
              <Footer />
            </Layout.Footer>
          </Layout>
          {ConfigHelper.isDev() && <DevToolsFrame />}
          <Sprite />
        </div>
      );
    }
  },
);

export default WrappedAppLayout;

import React from 'react';
import { connect } from 'react-redux';
import { Breadcrumb } from 'antd';
import { Link } from 'react-router-dom';
import _ from 'lodash';
import queryString from 'query-string';
import getCategoriesPath from 'src/helpers/getCategoriesPath';

interface IOwnProps {
  for: 'product' | 'catalog';
}

interface IConnectedState {
  categoriesPath: Alicanto.Models.Category[];
}

type IProps = IOwnProps & IConnectedState;

interface IState {}

const mapStateToProps = (state: Store.IState, ownProps: IOwnProps) => ({
  categoriesPath:
    (ownProps.for === 'product' && state.product.categories) ||
    (ownProps.for === 'catalog' &&
      getCategoriesPath(
        state.categoriesMenu.content,
        _.get(state.catalogQuery.filterObj, 'categories__in[0]', null),
      )) ||
    [],
});

const WrappedBreadcrumbs = connect<IConnectedState, undefined, IOwnProps>(mapStateToProps)(
  class Breadcrumbs extends React.Component<IProps, IState> {
    render() {
      const openCategories = [...this.props.categoriesPath];
      const selectedCategory = openCategories.pop();
      if (!selectedCategory) {
        return null;
      }
      return (
        <div className="root">
          <style jsx>{`
            .root :global(.ant-breadcrumb a) {
              color: #b2bdcb;
              font-size: 14px;
              letter-spacing: 2px;
              font-weight: bold;
            }
            .root :global(.ant-breadcrumb-separator) {
              margin: 0 14px;
            }
            .root :global(.ant-breadcrumb > span:last-child) {
              color: #062f49;
              font-size: 2em;
              font-weight: bold;
            }
          `}</style>
          <Breadcrumb separator=" ">
            {openCategories.map(category => (
              <Breadcrumb.Item key={category.id}>
                <Link
                  to={{
                    pathname: '/catalog',
                    search: queryString.stringify({
                      filter: JSON.stringify({
                        categories__in: [category.id],
                      }),
                    }),
                  }}
                >
                  {category.name}
                </Link>
              </Breadcrumb.Item>
            ))}
            <Breadcrumb.Item key={selectedCategory.id}>{selectedCategory.name}</Breadcrumb.Item>
          </Breadcrumb>
        </div>
      );
    }
  },
);

export default WrappedBreadcrumbs;

import React from 'react';
import { connect } from 'react-redux';
import { Row, Col, Button } from 'antd';
import _ from 'lodash';
import dispatch from 'src/store/dispatch';
import { checkoutUserOrder } from 'src/ducks/userOrders';
import CartItems from 'src/components/CartItems';
import Price from 'src/components/Price';

interface IOwnProps {}

interface IConnectedState {
  ui: Alicanto.Models.UI;
  items: Alicanto.Models.CatalogItem[];
  totalPrice: number;
}

type IProps = IOwnProps & IConnectedState;

interface IState {
  isMounted: boolean;
}

const mapStateToProps = (state: Store.IState) => ({
  ui: state.ui,
  items: state.cart.items,
  totalPrice: state.cart.totalPrice,
});

const WrappedCart = connect<IConnectedState, undefined, IOwnProps>(mapStateToProps)(
  class Cart extends React.Component<IProps, IState> {
    state: IState = {
      isMounted: false,
    };

    handleCheckout = () => {
      dispatch(checkoutUserOrder());
    };

    componentDidMount() {
      this.setState({ isMounted: true });
    }

    render() {
      // TODO ch1333
      const currency: string = _.get(this.props, 'items[0].schema_fields.currency.value', '');
      const cartUI = this.props.ui.routes.cart;
      return (
        <div className="root app-container">
          <style jsx>{`
            .root {
              padding-bottom: 30px;
            }
            .root :global(.total) {
              text-align: right;
              font-size: 10px;
              color: #b2bdcb;
            }
            .root :global(.price-wrapper) {
              margin-left: 5px;
              color: #062f49;
              font-size: 18px;
              font-weight: bold;
            }
            .cart-menu-wrapper {
              margin-bottom: 25px;
            }
            .button-wrapper {
              text-align: right;
            }
            .total {
              margin-bottom: 30px;
            }
          `}</style>
          {this.state.isMounted &&
            (_.isEmpty(this.props.items) ? (
              <h2 className="app-text-empty">{cartUI.emptyStatus.i18n.defaultText}</h2>
            ) : (
              <div>
                <div className="cart-menu-wrapper">
                  <CartItems
                    cssModificationClass="has-border-bottom"
                    mayDeleteItems={true}
                    mayChangeAmountItems={true}
                    size="large"
                  />
                </div>
                <Row type="flex" justify="end" align="middle">
                  <Col span={23} pull={1}>
                    <div className="total">
                      {this.props.ui.routes.cart.total.i18n.defaultText}:
                      <span className="price-wrapper">
                        <Price price={this.props.totalPrice} currency={currency} />
                      </span>
                    </div>
                  </Col>
                </Row>
                <div className="button-wrapper">
                  <Button type="primary" size="large" onClick={this.handleCheckout}>
                    {cartUI.checkout.i18n.defaultText}
                  </Button>
                </div>
              </div>
            ))}
        </div>
      );
    }
  },
);

export default WrappedCart;

import React from 'react';
import { connect } from 'react-redux';
import { InputNumber, Spin } from 'antd';
import dispatch from 'src/store/dispatch';
import { increaseQuantity, decreaseQuantity } from 'src/ducks/cart';

interface IOwnProps {
  mayChangeAmountItems: boolean;
  uid: string;
  count: number;
  pendingShowSpinning?: number;
  pendingHideSpinning?: number;
}

interface IConnectedState {}

type IProps = IOwnProps & IConnectedState;

interface IState {
  isSpinning: boolean;
}

const mapStateToProps = (state: Store.IState) => ({});

const WrappedAmount = connect<IConnectedState, undefined, IOwnProps>(mapStateToProps)(
  class Amount extends React.Component<IProps, IState> {
    static defaultProps = {
      pendingShowSpinning: 400,
      pendingHideSpinning: 300,
    };

    spinningTimeoutId: number;

    state: IState = {
      isSpinning: false,
    };

    showSpinning = () => {
      if (!!this.spinningTimeoutId) {
        window.clearTimeout(this.spinningTimeoutId);
        this.spinningTimeoutId = null;
      }
      this.spinningTimeoutId = window.setTimeout(() => {
        this.setState({ isSpinning: true });
      }, this.props.pendingShowSpinning);
    };

    hideSpinning = () => {
      if (!!this.spinningTimeoutId) {
        window.clearTimeout(this.spinningTimeoutId);
        this.spinningTimeoutId = null;
      }
      if (this.state.isSpinning) {
        this.spinningTimeoutId = window.setTimeout(() => {
          this.setState({ isSpinning: false });
        }, this.props.pendingHideSpinning);
      }
    };

    handleChangeAmount = (value: number) => {
      if (process.env.BROWSER) {
        const { uid, count } = this.props;
        this.showSpinning();
        if (count < value) {
          dispatch(
            increaseQuantity(uid, () => {
              this.hideSpinning();
            }),
          );
        } else {
          dispatch(
            decreaseQuantity(uid, () => {
              this.hideSpinning();
            }),
          );
        }
      }
    };

    componentWillUnmount() {
      if (process.env.BROWSER) {
        if (!!this.spinningTimeoutId) {
          window.clearTimeout(this.spinningTimeoutId);
        }
      }
    }

    render() {
      const { mayChangeAmountItems, count } = this.props;
      return (
        <div className="root">
          <style jsx>{`
            .root {
              display: inline-block;
              vertical-align: top;
            }
            .root :global(.ant-input-number) {
              border: 0;
              display: flex;
              justify-content: center;
              background: transparent;
              color: #062f49;
            }
            .root :global(.ant-input-number:hover) {
              border-color: transparent;
            }
            .root :global(.ant-input-number-focused) {
              box-shadow: none;
              border-color: transparent;
            }
            .root :global(.ant-input-number-handler-wrap) {
              border: 0;
              opacity: 1;
              width: 100%;
              display: flex;
              flex-direction: row-reverse;
              background: transparent;
            }
            .root :global(.ant-input-number-input-wrap) {
              position: relative;
              z-index: 3;
              width: 32px;
              height: 32px;
            }
            .root :global(.ant-input-number-handler) {
              width: 50%;
              height: 100%;
              border: 0;
              top: 0;
              bottom: 0;
              background: transparent;
            }
            .root :global(.ant-input-number-handler:hover) {
              height: 100% !important;
            }
            .root :global(.ant-input-number-handler-down-inner) {
              right: auto;
              left: 4px;
            }
            .root :global(.ant-input-number-input) {
              background-color: #e5ecf4;
              border-radius: 8px;
              color: inherit;
              text-align: right;
              font-weight: bold;
            }
          `}</style>
          {mayChangeAmountItems ? (
            <Spin spinning={this.state.isSpinning}>
              <InputNumber min={1} value={count} onChange={this.handleChangeAmount} />
            </Spin>
          ) : (
            count
          )}
        </div>
      );
    }
  },
);

export default WrappedAmount;

import React from 'react';
import { connect } from 'react-redux';
import { Table } from 'antd';
import cx from 'classnames';
import _ from 'lodash';
import Price from 'src/components/Price';
import CartProductImage from 'src/components/CartProductImage';
import Name from './Name';
import Amount from './Amount';
import Delete from './Delete';

interface IRecord {
  pictureUrl: string;
  nameFields: {
    name: string;
    shortDescription: string;
  };
  uid: string;
  key: string;
  count: number;
  cost: {
    currency: string;
    price: number;
  };
}

interface IOwnProps {
  mayDeleteItems?: boolean;
  mayChangeAmountItems?: boolean;
  footer?: React.ReactNode;
  cssModificationClass?: string;
  size?: 'large';
}

interface IConnectedState {
  ui: Alicanto.Models.UI;
  items: Alicanto.Models.CatalogItem[];
}

type IProps = IOwnProps & IConnectedState;

interface IState {}

const mapStateToProps = (state: Store.IState) => ({
  ui: state.ui,
  items: state.cart.items,
});

const WrappedCartItems = connect<IConnectedState, undefined, IOwnProps>(mapStateToProps)(
  class CartItems extends React.Component<IProps, IState> {
    getDataSource = (): IRecord[] => {
      return this.props.items.map(item => {
        const uid = item.item;
        const count = _.get(item, 'count', 0);
        const schemaFields = item.schema_fields;
        const pictureUrl = _.get(schemaFields, 'picture_url.value', '');
        const name = _.get(schemaFields, 'name.value', '');
        const shortDescription = _.get(schemaFields, 'short_description.value', '');
        const currency = _.get(schemaFields, 'currency.value', '');
        const price = _.get(schemaFields, 'price.value', 0);
        return {
          pictureUrl,
          count,
          uid,
          cost: { price, currency },
          nameFields: { shortDescription, name },
          key: uid,
        };
      });
    };

    render() {
      const { mayChangeAmountItems, mayDeleteItems, cssModificationClass, size, items, footer } = this.props;
      const cartUI = this.props.ui.routes.cart;
      const columns = [
        {
          key: 'image',
          className: 'image-col',
          render: (text: string, record: IRecord) => {
            const { pictureUrl, nameFields: { name } } = record;
            return <CartProductImage pictureUrl={pictureUrl} imageAlt={name} />;
          },
        },
        {
          key: 'name',
          className: 'name-col',
          render: (text: string, record: IRecord) => {
            const { uid, nameFields: { name, shortDescription } } = record;
            return <Name uid={uid} name={name} shortDescription={shortDescription} />;
          },
        },
        {
          key: 'count',
          className: 'count-col',
          render: (text: string, record: IRecord) => {
            const { uid, count } = record;
            return <Amount mayChangeAmountItems={mayChangeAmountItems} uid={uid} count={count} />;
          },
        },
        {
          key: 'cost',
          className: 'cost-col',
          render: (text: string, record: IRecord) => {
            const { price, currency } = record.cost;
            return <Price price={price} currency={currency} />;
          },
        },
        {
          key: 'delete',
          className: 'delete-col',
          render: (text: string, record: IRecord) => {
            const { uid } = record;
            return <Delete mayDeleteItems={mayDeleteItems} uid={uid} />;
          },
        },
      ];
      return (
        <div
          className={cx('root', {
            [cssModificationClass]: !!cssModificationClass,
            large: size === 'large',
          })}
        >
          <style jsx>{`
            .root {
              width: 540px;
              max-width: 100%;
              font-size: 14px;
              font-weight: bold;
              padding: 15px 0;
            }
            .large {
              width: 100%;
              padding-bottom: 0;
            }
            .root :global(.list) {
              margin-bottom: 16px;
            }
            .root :global(.name-col) {
              width: 100%;
              max-width: 0;
            }
            .root :global(.image-col) {
              padding-left: 0;
            }
            .root :global(.delete-col) {
              padding-right: 0;
            }
            .root :global(.cost-col) {
              text-align: right;
              white-space: nowrap;
            }
