import React from 'react';
import { RouterState, push } from 'connected-react-router';
import { Row, Col, Button, Spin, Modal } from 'antd';
import _ from 'lodash';
import queryString from 'query-string';
import dispatch from 'src/store/dispatch';
import { addToCart } from 'src/ducks/cart';
import Item from './Item';
import CartItems from 'src/components/CartItems';

interface IProps {
  router: RouterState;
  ui: Alicanto.Models.UI;
  isRequesting: boolean;
  items: Alicanto.Models.CatalogItem[];
  hasNextPage: boolean;
}

interface IState {
  isVisibleCartModal: boolean;
}

class ProductItems extends React.Component<IProps, IState> {
  state: IState = {
    isVisibleCartModal: false,
  };

  handleGoToCart = () => {
    dispatch(push('/cart'));
  };

  handleCartModalCancel = () => {
    this.setState({
      isVisibleCartModal: false,
    });
  };

  handleAddToCart = (item: Alicanto.Models.CatalogItem) => () => {
    dispatch(
      addToCart(item, () => {
        this.setState({
          isVisibleCartModal: true,
        });
      }),
    );
  };

  handleShowMore = () => {
    const query = queryString.parse(this.props.router.location.search);
    query.page = parseInt(_.get(query, 'page', '1'), 10) + 1;
    const location = { ...this.props.router.location, search: queryString.stringify(query) };
    dispatch(push(location));
  };

  render() {
    const commonUI = this.props.ui.common;
    return (
      <div className="root">
        <style jsx>{`
          .root {
            padding-bottom: 16px;
          }
          .root :global(.footer) {
            padding: 0 8px;
            text-align: center;
          }
        `}</style>
        <Row type="flex" justify="start">
          {this.props.items.map((item: Alicanto.Models.CatalogItem) => (
            <Col key={item.uid} xs={24} sm={12} md={8} lg={6} xl={6}>
              <Item ui={this.props.ui} item={item} handleAddToCart={this.handleAddToCart(item)} />
            </Col>
          ))}
        </Row>
        <Row type="flex" justify="center" align="middle" className="footer">
          <Col span={24}>
            {this.props.isRequesting ? (
              <Spin />
            ) : (
              this.props.hasNextPage && (
                <Button onClick={this.handleShowMore} size="large" className="app-show-more-button">
                  {commonUI.buttons.showMore.i18n.defaultText}
                </Button>
              )
            )}
          </Col>
        </Row>
        <Modal visible={this.state.isVisibleCartModal} onCancel={this.handleCartModalCancel} footer={false} width={540}>
          <CartItems
            mayChangeAmountItems={true}
            footer={[
              <Button
                key="continueShopping"
                className="button"
                type="primary"
                size="large"
                onClick={this.handleCartModalCancel}
              >
                {commonUI.buttons.continueShopping.i18n.defaultText}
              </Button>,
              <Button key="goToCart" className="button" type="primary" size="large" onClick={this.handleGoToCart}>
                {commonUI.buttons.goToCart.i18n.defaultText}
              </Button>,
            ]}
          />
        </Modal>
      </div>
    );
  }
}

export default ProductItems;

import React from 'react';
import { Layout, Button } from 'antd';
import cx from 'classnames';
import Svg from 'src/components/Svg';

interface IProps {
  type: 'categories' | 'filters';
}

interface IState {
  isFixed: boolean;
  isCollapsed: boolean;
}

class SiderMenu extends React.PureComponent<IProps, IState> {
  static __ANT_LAYOUT_SIDER = true;
  trigger: Button;
  state: IState = {
    isFixed: false,
    isCollapsed: true,
  };

  toggleCollapsed = (value: boolean) => () => {
    this.setState({
      isCollapsed: value,
    });
  };

  toggleFixed = (value: boolean) => {
    this.setState({
      isFixed: value,
    });
  };

  handleScroll = () => {
    const scrolled = window.pageYOffset || document.documentElement.scrollTop;
    const limit = 115;
    if (scrolled >= limit) {
      this.toggleFixed(true);
    } else {
      this.toggleFixed(false);
    }
  };

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  render() {
    return (
      <div
        className={cx('root', {
          fixed: this.state.isFixed,
          collapsed: this.state.isCollapsed,
          [this.props.type]: !!this.props.type,
        })}
        onMouseLeave={this.toggleCollapsed(true)}
        onMouseEnter={this.toggleCollapsed(false)}
      >
        <style jsx>{`
          .root {
            position: relative;
            z-index: 10;
            min-width: 98px;
            background-color: #fff;
          }
          .root :global(.ant-layout-sider-children) {
            box-shadow: none;
          }
          .root :global(.ant-layout-sider) {
            max-height: 100%;
            overflow-x: auto;
          }
          .inner-wrapper {
            position: absolute;
            top: -1px;
            min-height: 100vh;
            height: 100%;
            padding-top: 15px;
            padding-bottom: 40px;
            background-color: #fff;
            border: 1px solid transparent;
            box-shadow: 0 2px 16px 0 rgba(36, 103, 164, 0.18);
            transition: box-shadow 0.2s;
          }
          .categories :global(.inner-wrapper) {
            left: 0;
          }
          .filters :global(.inner-wrapper) {
            right: 0;
          }
          .collapsed :global(.inner-wrapper) {
            box-shadow: none;
            border-color: #e5ecf4;
          }
          .fixed :global(.inner-wrapper) {
            position: fixed;
          }
          .button-wrapper {
            padding-top: 35px;
            text-align: center;
            position: relative;
            z-index: 10;
            opacity: 0;
          }
          .categories :global(.button-wrapper) {
            left: 0;
          }
          .filters :global(.button-wrapper) {
            right: 0;
          }
          .collapsed :global(.button-wrapper) {
            transition: opacity 2s;
            opacity: 1;
          }
          .fixed :global(.button-wrapper) {
            position: fixed;
            top: 0;
            width: 98px;
          }
          .collapsed :global(.app-main-menu .footer) {
            width: 0;
          }
          .collapsed :global(.app-main-menu) {
            max-width: 0;
          }
        `}</style>
        <div className="button-wrapper">
          <Button shape="circle">
            <Svg name={this.props.type === 'categories' ? 'menu' : 'filter'} className="app-icon-svg" />
          </Button>
        </div>
        <div className="inner-wrapper">
          <Layout.Sider trigger={null} collapsedWidth="98" width="260" collapsible collapsed={this.state.isCollapsed}>
            {this.props.children}
