import { createAction, createReducer } from 'redux-act';
import _ from 'lodash';
import { BusinessLogicError } from 'src/store/common';
import { deleteCart } from 'src/ducks/cart';
import { AxiosResponse } from 'axios';
import { push } from 'connected-react-router';
import { GET, POST } from 'src/core/API';
import EndpointsConfig from 'src/configs/endpoints';

export const REDUCER = 'USER_ORDERS';
const NS = `${REDUCER}__`;

export interface IUserOrdersState extends Alicanto.API.GET_USER_ORDERS_RESPONSE, Store.IReduxCommonState {
  currentPaymentId: number;
}

export const initialState: IUserOrdersState = {
  count: 0,
  next: null,
  previous: null,
  results: [],
  currentPaymentId: null,
  isRequestError: false,
  isRequesting: false,
};

const reducer = createReducer<IUserOrdersState>({}, initialState);

const getFailure = createAction(`${NS}GET_FAILURE`);
reducer.on(getFailure, state => ({
  ...state,
  isRequestError: true,
  isRequesting: false,
}));

const getSuccess = createAction<Alicanto.API.GET_USER_ORDERS_RESPONSE>(`${NS}GET_SUCCESS`);
reducer.on(getSuccess, (state, { next, previous, count, results }) => ({
  ...state,
  next,
  previous,
  count,
  results: [...results],
  isRequestError: false,
  isRequesting: false,
}));

const getRequest = createAction(`${NS}GET_REQUEST`);
reducer.on(getRequest, state => ({
  ...state,
  isRequestError: false,
  isRequesting: true,
}));

const postFailure = createAction(`${NS}POST_FAILURE`);
reducer.on(postFailure, state => ({
  ...state,
  count: 0,
  next: null,
  previous: null,
  results: [],
  currentPaymentId: null,
  isRequestError: true,
  isRequesting: false,
}));

const postSuccess = createAction(`${NS}POST_SUCCESS`);
reducer.on(postSuccess, state => ({
  ...state,
  isRequestError: false,
  isRequesting: false,
}));

const postRequest = createAction(`${NS}POST_REQUEST`);
reducer.on(postRequest, state => ({
  ...state,
  isRequestError: false,
  isRequesting: true,
}));

const setCurrentPaymentId = createAction<number>(`${NS}SET_CURRENT_PAYMENT_ID`);
reducer.on(setCurrentPaymentId, (state, currentPaymentId) => ({
  ...state,
  currentPaymentId,
}));

interface IUserOrdersQuery {
  page?: string;
  page_size?: string;
}

const getUserOrdersRequestParams = ({ page, page_size }: IUserOrdersQuery) => {
  const params: IUserOrdersQuery = {
    page_size: '3',
  };
  if (!_.isNil(page)) {
    params.page = page;
  }
  if (!_.isNil(page_size)) {
    params.page_size = page_size;
  }
  return params;
};

export const getUserOrders = (page = '1'): Store.IReduxThunkCb => (dispatch, getState, xShopHeaders) => {
  return new Promise((resolve, reject) => {
    dispatch(getRequest());
    GET(
      {
        url: `${EndpointsConfig.USER_ORDERS}`,
        params: getUserOrdersRequestParams({ page }),
      },
      xShopHeaders,
    )
      .then((resp: AxiosResponse) => {
        const data = resp.data as Alicanto.API.GET_USER_ORDERS_RESPONSE;
        let results = data.results;
        results = results.map((item: Alicanto.Models.Order) => ({
          ...item,
          cart: {
            ...item.cart,
            total: parseFloat(_.get(item.cart, 'total', '0')),
          },
        }));
        if (page !== '1') {
          const oldResults = getState().userOrders.results;
          results = [...oldResults, ...results];
        }
        dispatch(getSuccess({ ...data, results }));
        resolve();
      })
      .catch(error => {
        dispatch(getFailure());
        reject(error);
      });
  }).then(() => {
    const currentPaymentId = getState().userOrders.currentPaymentId;
    if (process.env.BROWSER && _.isNull(currentPaymentId)) {
      const lastPaymentTimeInMs = window.localStorage.getItem('paymentTime');
      const lastPaymentId = window.localStorage.getItem('paymentId');
      if (lastPaymentTimeInMs && lastPaymentId) {
        const msNow = Date.now();
        const passingTime = msNow - parseInt(lastPaymentTimeInMs, 10);
        if (passingTime < 30 * 1000) {
          dispatch(setCurrentPaymentId(parseInt(lastPaymentId, 10)));
          setTimeout(() => {
            dispatch(setCurrentPaymentId(null));
            window.localStorage.removeItem('paymentTime');
            window.localStorage.removeItem('paymentId');
          }, 30 * 1000 - passingTime);
        }
      }
    }
  });
};

export const checkoutUserOrder = (): Store.IReduxThunkCb => (dispatch, getState, xShopHeaders) => {
  return new Promise((resolve, reject) => {
    const isAuthenticated = getState().auth.isAuthenticated;
    if (!isAuthenticated) {
      const anonCheckout = getState().ui.routes.cart.errors.anonCheckout.i18n.defaultText;
      const error = new BusinessLogicError(anonCheckout);
      reject(error);
      return;
    }
    dispatch(postRequest());
    const data: Alicanto.API.POST_USER_ORDERS_BODY = { cart: getState().cart.id };
    POST(
      {
        data,
        url: EndpointsConfig.USER_ORDERS,
      },
      xShopHeaders,
    )
      .then((resp: AxiosResponse) => {
        dispatch(deleteCart());
        dispatch(postSuccess());
        if (process.env.BROWSER) {
          //  TODO: move to external?
          dispatch(push('/user/orders'));
        }
        resolve();
      })
      .catch(error => {
        dispatch(postFailure());
        reject(error);
      });
  });
};

export const payAnOrder = (id: number): Store.IReduxThunkCb => (dispatch, getState, xShopHeaders) => {
  return new Promise((resolve, reject) => {
    const currentPaymentId = getState().userOrders.currentPaymentId;
    if (currentPaymentId) {
      const ui = getState().ui;
      reject(new BusinessLogicError(ui.routes.user.orders.paymentWarning.i18n.defaultText));
      return;
    }
    if (process.env.BROWSER) {
      window.localStorage.setItem('paymentTime', '' + Date.now());
      window.localStorage.setItem('paymentId', `${id}`);
    }
    dispatch(setCurrentPaymentId(id));
    setTimeout(() => {
      dispatch(setCurrentPaymentId(null));
      if (process.env.BROWSER) {
        window.localStorage.removeItem('paymentTime');
        window.localStorage.removeItem('paymentId');
      }
    }, 30 * 1000);
    dispatch(postRequest());
    const data: Alicanto.API.POST_PAYMENTS_PAY_BODY = { order: id + '' };
    POST(
      {
        data,
        url: EndpointsConfig.PAY_ORDER,
      },
      xShopHeaders,
    )
      .then((resp: AxiosResponse) => {
        dispatch(postSuccess());
        const data = resp.data as Alicanto.API.POST_PAYMENTS_PAY_RESPONSE;
        const redirect_url = data.redirect_to.redirect_url;
        if (process.env.BROWSER) {
          window.location.assign(redirect_url);
        }
        resolve();
      })
      .catch(error => {
        dispatch(postFailure());
        reject(error);
      });
  });
};

export default reducer;

const _ENV_CONSTANTS = {
  DEV: 'development',
  PROD: 'production',
  STAGING: 'staging',
  TEST: 'test',
  UNSTABLE: 'unstable',
};

const _ENV = process.env.NODE_ENV || _ENV_CONSTANTS.DEV;

const getEnv = () => _ENV;

const isDev = () => _ENV === _ENV_CONSTANTS.DEV;

const isProd = () => _ENV === _ENV_CONSTANTS.PROD;

const isTest = () => _ENV === _ENV_CONSTANTS.TEST;

const isStaging = () => _ENV === _ENV_CONSTANTS.STAGING;

const isUnstable = () => _ENV === _ENV_CONSTANTS.UNSTABLE;

const getEnvConstants = () => _ENV_CONSTANTS;

export default {
  getEnv,
  isDev,
  isProd,
  isTest,
  isStaging,
  isUnstable,
  getEnvConstants,
};

import _ from 'lodash';

const getCategoriesPath = (categories: Alicanto.Models.Category[], selectedId: number) => {
  let result: Alicanto.Models.Category[] = [];
  if (!_.isNull(selectedId)) {
    categories.find(category => {
      if (_.isEmpty(category.subcategories)) {
        if (category.id === selectedId) {
          result = [category];
          return true;
        }
      } else {
        const children = getCategoriesPath(category.subcategories, selectedId);
        if (!_.isEmpty(children)) {
          result = [category, ...children];
          return true;
        }
      }
      return false;
    });
  }
  return result;
};

export default getCategoriesPath;

const normalizePictures = (pictures: Alicanto.Models.Picture[]) => {
  const result: Alicanto.Models.Picture[] = [];
  if (pictures) {
    pictures.forEach(picture => {
      if (picture.is_main) {
        result.unshift(picture);
      } else {
        result.push(picture);
      }
    });
  }
  return result;
};

export default normalizePictures;

import ConfigHelper from 'src/helpers/configHelper';
import _ from 'lodash';

export const safelyParseJSON = (text: string) => {
  let result = null;
  if (!_.isNull(text)) {
    try {
      result = JSON.parse(text);
    } catch (error) {
      if (ConfigHelper.isDev()) {
        console.error(error);
      } else if (process.env.BROWSER) {
        const logException = require('src/helpers/sentryClient').logException;
        logException(error);
      } else {
        const logger = require('src/server/logger/logger').logger;
        logger.error(error);
      }
    }
  }
  return result;
};

import Raven from 'raven-js';
import CommonCfg from 'src/configs/common';
import ReactCfg from 'src/configs/react';

export const sentryCfg = `https://${ReactCfg.SENTRY_KEY_REACT}@app.getsentry.com/${CommonCfg.SENTRY_PROJECT_ID}`;

export function logException(ex: Error, context?: object) {
  Raven.captureException(ex, {
    extra: context,
  });

  // tslint:disable-next-line:no-unused-expression
  window &&
    window.console &&
    console.error &&
    // tslint:disable-next-line:no-console
    console.error(ex);
}

import _ from 'lodash';
import ServerCfg from 'src/configs/server';
import { IReactRouteRequest } from 'src/core/interfaces/IReactRouteRequest';

const extractHostname = (url: string) => {
  let hostname;

  // find & remove protocol (http, ftp, etc.) and get the hostname
  hostname = url.indexOf('://') > -1 ? url.split('/')[2] : url.split('/')[0];

  // find & remove port number
  hostname = hostname.split(':')[0];

  return hostname;
};

const getDomain = (req: IReactRouteRequest) => {
  const xShopDomain = _.get(req, 'headers.x-shop-domain', null);

  let originHost = _.get(req, 'headers.origin', null);

  if (originHost) {
    originHost = extractHostname(originHost);
  }

  let host = req.get('host');

  if (host && host.indexOf(':') !== -1) {
    host = host.split(':')[0];
  }

  const result = xShopDomain || originHost || host;

  if (result === ServerCfg.HOST) {
    throw new Error('Invalid domain, may be xShopHeaders is not set in API call.');
  }

  return result;
};

export default {
  getDomain,
};

import { AxiosError } from 'axios';
import _ from 'lodash';
import { message } from 'antd';
import { BaseError } from 'make-error';

export class BusinessLogicError extends BaseError {}

const beautifyMessage = (message: object) => {
  try {
    return JSON.stringify(message).replace(/"/g, '');
  } catch (e) {
    return 'unknown message text';
  }
};

export const showError = (error: AxiosError | BusinessLogicError) => {
  if (process.env.BROWSER) {
    if (error instanceof BusinessLogicError) {
      message.error(error.message);
      return;
    }
    const responseStatus = _.get(error, 'response.status', 'unknown status');
    const responseData = _.get(error, 'response.data', null);
    const responseBody = _.get(error, 'response.body', null);
    const simpleError = _.get(responseData, 'error', null);
    const errorMessage = _.get(simpleError, 'message', null);
    const errorDetails = _.get(simpleError, 'detail', null);
    const errorsObject = _.get(simpleError, 'errors', null);
    if (!responseData && !responseBody) {
      message.error(`Status code: ${responseStatus}. Message: unknown message text`);
    } else if (errorMessage) {
      message.error(`Status code: ${responseStatus}. Message: ${errorMessage}`);
    } else if (errorDetails) {
      message.error(`Status code: ${responseStatus}. Message: ${errorDetails}`);
    } else if (errorsObject) {
      message.error(`Status code: ${responseStatus}. Message: ${beautifyMessage(errorsObject)}`);
    } else if (simpleError) {
      message.error(`Status code: ${responseStatus}. Message: ${beautifyMessage(simpleError)}`);
    } else if (!_.isNull(responseData)) {
      message.error(`Status code: ${responseStatus}. Message: ${beautifyMessage(responseData)}`);
    } else if (!_.isNull(responseBody)) {
      message.error(`Status code: ${responseStatus}. Message: ${beautifyMessage(responseBody)}`);
    } else {
      message.error(`Status code: ${responseStatus}. Message: ${beautifyMessage(error)}`);
    }
  }
};

